mapper.insert:

 			SELECT IFNULL(MAX(notice_idx), 0) FROM notice


		INSERT
			INTO notice
			VALUES (
				#{notice_idx} + 1
				,#{notice_title}
				,#{notice_content}
				,now()
				,#{notice_file}
				,'파일경로하드코딩'
			)

mapper.select:
	SELECT *
 		FROM notice
 		ORDER BY notice_idx DESC;

mapper.update:
UPDATE notice
	SET
	   notice_title = #{notice_title}
	  ,notice_content = #{notice_content}
	  ,notice_file = #{notice_file}
	WHERE notice_idx = #{notice_idx};
