mapper.insert:


	SELECT IFNULL(MAX(owner_idx), 0) FROM owner 


INSERT
	INTO owner
	VALUES (
		#{owner_idx} + 1
		,#{owner_id}
		,#{owner_passwd}
		,#{owner_name}
		,#{owner_phone}
		,#{owner_email}
		,now()
	)

mapper.select:
SELECT IF(COUNT(*) = 1, 1, 0)
	FROM owner
	WHERE owner_id = #{owner_id} AND owner_passwd = #{owner_passwd}
