mapper.insert:


	SELECT IFNULL(MAX(member_idx), 0) FROM member 


INSERT
	INTO member
	VALUES (
		#{member_idx} + 1
		,'o'
		,#{member_name}
		,#{member_id}
		,#{member_passwd}
		,#{member_phone}
		,#{member_email}
		,#{member_birth}
		,#{member_gender}
		,now()
	)

mapper.select:
SELECT IF(COUNT(*) = 1, 1, 0)
	FROM member
	WHERE member_id = #{member_id} AND member_passwd = #{member_passwd}

SELECT member_passwd
		FROM member
			WHERE member_id = #{sId}

mapper.update:
