mapper.insert:


		SELECT IFNULL(MAX(store_idx), 0) FROM store 



 	INSERT
 		INTO store
 		VALUES (
 			#{store_idx} + 1
 			,#{store_name} 
 			,#{store_phone} 
 			,#{store_address}  
 			,#{store_type}
 			,#{store_content}
 			,#{store_time}  
 			,#{store_license} 
 			,now()
 			,#{store_etc}
 			,#{store_file}
 			,#{store_file_path}
 			,#{member_id} 
 		)
